var gp = require('./lib');
var cheerio = require('cheerio');

gp.load({
	timeout: 1000,
	startURL: 'http://www.legasoft.ru/torgovoe-oborudovanie/skaneri-shtrih-kodov/',
	reg: /http\:\/\/www\.legasoft\.ru\/torgovoe\-oborudovanie\/skaneri\-shtrih\-kodov\/(.+)\//mg,
	handler: function (content, callback) {
		var $ = cheerio.load(content);
		callback({
			title: $('h1').html().trim()
		});
	},
	progress: function (count) {
		console.log(count);
	}
}, function (pages) {
	console.log(pages);
});